import React, { useEffect, useRef } from "react";

const ErrorForm = ({ msg }) => {
    return msg.lenght === 0
        ? null
        : msg.map((item, index) => (
              <div key={index} className="msg">
                  <i className="fa fa-check-circle" />
                  <div className="wrap-ms">
                      <span className="wrap-ms-title">Sucess</span>
                      <span className="wrap-ms-content">{item}</span>
                  </div>
                  <i className="fa fa-times"></i>
              </div>
          ))();
};

export default ErrorForm;

export const MemoErrorForm = React.memo(ErrorForm);
