import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { userLogout } from "../../Redux/Auth/authAction";
import { AllCartRequest } from "../../Redux/Cart/actionCard";
import {
    getProsByCateId,
    searchProduct,
} from "../../Redux/ProductRedux/actionProduct";
import { v4 as uuid4 } from "uuid";

const Header = () => {
    const history = useHistory();
    const dispatch = useDispatch();
    const { isAuth, user, isLoad } = useSelector((state) => state.userAuth);
    const { cart } = useSelector((state) => state.cartReducer);

    useEffect(() => {
        if (isAuth) dispatch(AllCartRequest());
    }, [isAuth]);

    const onLogOut = async () => {
        await dispatch(userLogout());
        history.push("/");
    };

    const searchVal = React.createRef();
    const listSea = React.createRef();

    const [valInput, setValInput] = useState("");
    const [store, setStore] = useState({
        new: [],
        old: [],
    });
    useEffect(() => {
        const search = localStorage.getItem("search");
        const data = search ? JSON.parse(search) : [];
        setStore({
            ...store,
            old: data,
        });
    }, [localStorage.getItem("search")]);

    const handleChangeInput = async (e) => {
        const text = e.target.value;
        setValInput(text);
        if (text !== "" && text.length >= 2) {
            let { data } = await dispatch(searchProduct(text));
            setStore({
                ...store,
                new: data,
            });
        } else if (text === "") setStore({ ...store, new: [] });
    };

    const addLocalStore = () => {
        let oldArr = [];
        let local = localStorage.getItem("search");
        if (local) {
            let data = JSON.parse(local);
            let check = data.includes(valInput);
            if (!check) oldArr = [...data, valInput];
            else oldArr = [...data];
        }

        const arr = JSON.stringify(oldArr);
        localStorage.setItem("search", arr);

        history.push(`/page/search?q=${valInput}`);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const list = e.target.parentElement.querySelector("ul");
        list.classList.remove("show-list");
        addLocalStore();
    };

    const handleClickItemSearch = (name) => {
        // console.log(name);
        alert(name);
        // window.location.href = `/page/search?q=${name}`;
    };

    useEffect(() => {
        let t = 0;
        const searchInput = document.querySelector(".header_body-search-input");
        const list = document.querySelector(".header_list-search");

        searchInput.addEventListener("focus", () => {
            list.classList.add("show-list");
        });
        searchInput.addEventListener("blur", () => {
            if (t === 0) list.classList.remove("show-list");
        });
        list.addEventListener("mouseover", function () {
            t = 1;
            // this.classList.add = "d-block";
            // searchInput.focus();
        });
        list.addEventListener("mouseleave", () => {
            t = 0;
        });
    });

    useEffect(() => {
        const listNoti = document.querySelector(".header_notifi-eff");
        listNoti.parentElement.addEventListener("mouseenter", () => {
            listNoti.style.animation = "fade-in linear 0.2s forwards";
        });
        listNoti.parentElement.addEventListener("mouseleave", () => {
            listNoti.style.animation = "fade-out linear 0.2s forwards";
        });
        // } else if (!isLoad && !user) {
        //     const listEmpty =
        //         document.parentElement.querySelector(".header_nav-notifi");
        //     listEmpty.addEventListener("mouseenter", () => {
        //         listEmpty.style.animation = "fade-in linear 0.2s forwards";
        //     });
        //     listEmpty.parentElement.addEventListener("mouseleave", () => {
        //         listEmpty.style.animation = "fade-out linear 0.2s forwards";
        //     });
        // }
    }, [isAuth]);

    let style =
        store.new.length > 0 || store.new.lenght > 0
            ? {
                  border: "1px solid #d6cece9c",
                  boxShadow: "0 0 4px 1px #d7d0d09e",
              }
            : {};
    return (
        <header>
            <div className="app grid wide">
                <div className="header_nav">
                    {/* <!--mt-2 here have a error --> */}
                    <ul className="header-nav-list header_nav-left ms-0 mt-2 mb-2">
                        <li
                            className="
                                header_nav-item
                                show
                                me-4
                                text-white
                                position-relative
                            "
                        >
                            <Link
                                to="#"
                                className="header_nav-link header_nav-link-download"
                            >
                                Tải ứng dụng
                            </Link>
                            <div
                                className="
                                    header_nav-app
                                    rounded
                                    bridge bridge-left bridge-custom-download
                                "
                            >
                                <div
                                    className="header_nav-app-qrcode w-100 rounded"
                                    style={{
                                        backgroundImage:
                                            "url('./public/img/qrcode.png')",
                                    }}
                                ></div>
                                <div className="header_nav-app-wrap">
                                    <img
                                        className="header_nav-link"
                                        src="./public/img/appStore.png"
                                        alt=""
                                    />
                                    <img
                                        className="header_nav-link"
                                        src="./public/img/ggplay.png"
                                        alt=""
                                    />
                                </div>
                            </div>
                        </li>
                        <li
                            className="
                                header_nav-item header_nav-item-not-effect
                                me-4
                                text-white
                                seperate
                            "
                        >
                            <span>Kết nối</span>
                            <i
                                className="
                                    header_nav-icons
                                    mx-1
                                    fab
                                    fa-facebook
                                    cursor-pointer
                                    mx-2
                                "
                            ></i>
                            <i
                                className="
                                    header_nav-icons
                                    me-1
                                    fab
                                    fa-instagram
                                    cursor-pointer
                                "
                            ></i>
                        </li>
                    </ul>
                    <ul
                        className="
                            header-nav-list header_nav-right
                            ms-0
                            mt-2
                            mb-2
                            rounded
                        
                            
                        "
                    >
                        {/* has user add class : use-user */}
                        <li
                            className="
                                header_nav-item
                                header_nav-item-show
                                me-4
                                text-white
                                position-relative
                            "
                        >
                            {/* <!-- header_nav-item-notifi  to show when not produces --> */}
                            <Link to="#" className="header_nav-link">
                                <i className="header_nav-icons me-1 far fa-bell"></i>
                                Thông báo
                            </Link>
                            {!user && (
                                <ul
                                    className="
                                    header_nav-notifi header_notifi-eff header_nav-notifi-has
                                    arrow-top
                                    rounded
                                    bridge bridge-right bridge-custom-notifi
                                    cursor-pointer
                                "
                                >
                                    <div className="header_nav-notify-not-produces">
                                        <img
                                            src="./public/img/99e561e3944805a023e87a81d4869600.png"
                                            alt=""
                                        />
                                        <span className="text-muted d-block mt-2">
                                            Đăng nhập để xem thông báo
                                        </span>
                                    </div>

                                    <div className="header_nav-auth d-flex use-user">
                                        {/* case has user add class : use-use */}
                                        <Link
                                            to="/signup"
                                            className="
                                            header_nav-auth-btn
                                            btn btn-light
                                            flex-fill
                                            rounded-0
                                            border-0
                                            d-block
                                        "
                                        >
                                            Đăng ký
                                        </Link>
                                        <Link
                                            to="/login"
                                            className="
                                            header_nav-auth-btn
                                            btn btn-light
                                            flex-fill
                                            rounded-0
                                            border-0
                                            d-block
                                        "
                                        >
                                            Đăng nhập
                                        </Link>
                                    </div>
                                </ul>
                            )}
                            {user && (
                                <ul className="header_notifi-list header_notifi-eff arrow-top rounded bridge bridge-right bridge-custom-notifi">
                                    <h2 className="text-muted fs-6 p-2 fw-normal mb-0">
                                        Thông báo mới nhận
                                    </h2>
                                    <li className="header_notifi-list-item active">
                                        <Link
                                            to="#"
                                            className="
                                            header_notifi-list-link
                                            d-flex
                                            align-items-center
                                            text-decoration-none
                                        "
                                        >
                                            <img
                                                className="mx-2 header_notifi-list-img"
                                                height="30px"
                                                width="30px"
                                                src="https://www.pngkey.com/png/detail/357-3576955_06-nov-2018-login-successful.png"
                                                alt=""
                                            />

                                            <div
                                                className="
                                                header_notifi-list-wrap
                                                flex-fill
                                            "
                                            >
                                                <h5
                                                    className="
                                                    header-notifi-list-title
                                                    mb-0
                                                    fs-6
                                                    text-secondary
                                                "
                                                >
                                                    Tài khoản đã được xác thực
                                                </h5>
                                                <span
                                                    className="
                                                    header_notifi-list-text
                                                    text-muted
                                                    small
                                                "
                                                >
                                                    Tài khoản của bạn đã được
                                                    kích hoạt. Now! Cùng khám
                                                    đừng phá một vòng web nào.
                                                    Mọt ý kiến chỉ trích xin gửi
                                                    về đồn công an gần nhà bạn
                                                    nhất
                                                </span>
                                            </div>
                                        </Link>
                                    </li>

                                    <li>
                                        <button className="btn btn-sm btn-light text-secondary text-center w-100">
                                            Xem tất cả
                                        </button>
                                    </li>
                                </ul>
                            )}
                        </li>
                        <li className="header_nav-item me-4 text-white">
                            <Link to="#" className="header_nav-link">
                                <i
                                    className="
                                        header_nav-icons
                                        me-1
                                        far
                                        fa-question-circle
                                    "
                                ></i>
                                Hỗ trợ
                            </Link>
                        </li>

                        {isAuth === false && (
                            <>
                                <li className="header_nav-item header_nav-item-auth me-4 text-white">
                                    <Link
                                        to="/signup"
                                        className="header_nav-link"
                                    >
                                        Đăng Ký
                                    </Link>
                                </li>
                                <li className="header_nav-item header_nav-item-auth me-4 text-white seperate">
                                    <Link
                                        to="/login"
                                        className="header_nav-link"
                                    >
                                        Đăng Nhập
                                    </Link>
                                </li>
                            </>
                        )}
                        {isAuth === true && user && (
                            <li className="header_nav-item header_nav-auth-account d-flex justify-content-center align-items-center position-relative">
                                <span
                                    className="header_nav-auth-account-img me-2"
                                    style={{
                                        backgroundImage: `url('${user.avata}')`,
                                    }}
                                ></span>
                                <span className="header_nav-auth-account-name text-white">
                                    {user.userName}
                                </span>
                                <div className="header_nav-account-options dropdown-menu bridge arrow-top">
                                    <div className="dropdown-item cursor-pointer">
                                        Tài khản của tôi
                                    </div>
                                    <div className="dropdown-item cursor-pointer">
                                        <Link
                                            className="text-decoration-none"
                                            to="/orders/status"
                                        >
                                            Đơn mua
                                        </Link>
                                    </div>
                                    <div className="dropdown-divider"></div>
                                    <div
                                        onClick={onLogOut}
                                        className="dropdown-item cursor-pointer"
                                    >
                                        {/* <Link
                                            className="text-decoration-none text-muted"
                                            to="/logout"
                                        >
                                            Đăng xuất
                                        </Link> */}
                                        Đăng xuất
                                    </div>
                                </div>
                            </li>
                        )}
                    </ul>
                </div>
                <div
                    className="
                        header_body
                        d-flex
                        justify-content-between
                        align-items-center
                    "
                >
                    <div className="header_body-logo">
                        <Link className="text-decoration-none" to="/" alt="">
