import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import bkg from "../../public/img/background.png";
import { userContext } from "../Context/userProvider";
import ErrorForm from "../Form/ErrorForm";

const Login = () => {
    const [loginState, setLoginState] = useState({
        email: "",
        password: "",
    });
    const [msg, setMsg] = useState(null);

    const { LoginForm } = useContext(userContext);

    const { email, password } = loginState;

    const onChangeInputForm = (e) => {
        setLoginState({ ...loginState, [e.target.name]: e.target.value });
    };

    const onSumbmitFormLogin = async (e) => {
        e.preventDefault();
        const res = await LoginForm(loginState); //
        if (res.success) {
        } else {
            setMsg([res.msg]); //
        }
    };

    const RemoveItem = (i) => {
        const newMsg = msg.filter((item, index) => {
            return index !== i;
        });
        setMsg([...newMsg]);
    };

    return (
        <>
            <div className="app-main">
                <div className="msg-wrap">
                    <ErrorForm msg={msg} removeItem={RemoveItem} />
                </div>
                <img
                    src={bkg}
                    alt="background"
                    width="1200px"
                    className="app-bkg"
                />
                <div className="grid wide app-container">
                    <div className="app-signup l-o-7 m-o-4">
                        <form
                            action="#"
                            method="POST"
                            onSubmit={onSumbmitFormLogin}
                        >
                            <h1 className="app-title">
                                Log In{" "}
                                <Link to="/signup" className="or-login">
                                    or Sign up
                                </Link>
                            </h1>
                            <div className="sign-other-way">
                                <div className="other-way-btn btn-gg">
                                    <i className="fab fa-google-plus"></i>
                                    &nbsp; Google+
                                </div>
                                <div className="other-way-btn btn-fb">
                                    <i className="fab fa-facebook"></i>{" "}
                                    &nbsp;Facebook
                                </div>
                            </div>
                            <div className="input-wrap">
                                <label className="form-lable" htmlFor="email">
                                    Email
                                </label>
                                <input
                                    type="text"
                                    name="email"
                                    className="form-input"
                                    id="email"
                                    placeholder="Nhập email"
                                    value={email}
                                    onChange={onChangeInputForm}
                                />
                                <span className="email-err form-err"></span>
                            </div>

                            <div className="input-wrap">
                                <label
                                    className="form-lable"
                                    htmlFor="password"
                                >
                                    Nhập mật khẩu
                                </label>
                                <input
                                    type="password"
                                    name="password"
                                    className="form-input"
                                    id="password"
                                    placeholder="Nhập mật khẩu"
                                    value={password}
                                    onChange={onChangeInputForm}
                                />
                                <span className="password-err form-err"></span>
                            </div>
                            <button className="btn btn_ful">Đăng Nhập</button>
                            <div className="plugin-login">
                                <div className="remember-account">
                                    <input type="checkbox" id="check" />
                                    <label
                                        htmlFor="check"
                                        className="form-label"
                                    >
                                        Remember me
                                    </label>
                                </div>
                                <a href="#" className="forgot-pass">
                                    Forget password??
                                </a>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Login;
