const handleNum = (num) => {
    if (num) {
        let rel = [];
        num = typeof num === "number" ? num.toString() : num;
        let tar = num.split("").reverse();
        tar.forEach((item, index) => {
            if (index % 3 === 0 && index !== 0) rel.push(",", item);
            else rel.push(item);
        });
        return rel.reverse().join("");
    }
};

export default handleNum;
