import React, { useEffect } from "react";

const Carousel = () => {
    useEffect(() => {
        const container = document.querySelector("carousel");
        const slide = document.querySelector(".carousel-inner");
        let slides = document.querySelectorAll(".carrousel-slide");
        const preBtn = document.querySelector(".pre");
        const nextBtn = document.querySelector(".next");
        const interval = 2000;
        let index = 1;
        let slideId;

        const lastSlide = slides[slides.length - 1].cloneNode(true);
        const firstSlide = slides[0].cloneNode(true);

        slide.appendChild(firstSlide);
        slide.insertBefore(lastSlide, slide[0]);

        const widthSlide = slides[index].clientWidth;
        slide.style.transform = `translateX(-${widthSlide}px)`;

        const startSlide = () => {
            slideId = setInterval(() => {
                index++;
                slide.style.transform = `translateX(-${widthSlide * index})`;
            });
        };
    }, []);

    return (
        <div className="carousel">
            <div className="carousel-inner">
                <div className="carousel-slide">
                    <img src="./public/img/banner1.jpg" alt="" />
                </div>
                <div className="carousel-slide">
                    <img src="./public/img/banner2.jpg" alt="" />
                </div>
                <div className="carousel-slide">
                    <img src="./public/img/banner3.jpg" alt="" />
                </div>
                <div className="carousel-slide">
                    <img src="./public/img/banner4.jpg" alt="" />
                </div>
                <div className="carousel-slide">
                    <img src="./public/img/banner5.jpg" alt="" />
                </div>
            </div>
            <div className="carousel-control">
                <span className="ctl pre"></span>
                <span className="ctl next"></span>
            </div>
        </div>
    );
};

export default Carousel;
