const Authorization = ({ formName, rules }) => {
    const form = document.forms[formName];

    const validation = (eleInput, rule) => {
        const ipErr = eleInput.nextElementSibling;
        const ipValue = eleInput.value;

        const isValid = rule(ipValue);

        if (isValid) {
            ipErr.textContent = isValid;
        }
    };

    if (form) {
        rules.forEach(({ ele, rule }) => {
            const ipForm = form.querySelector(`#${ele}`);

            if (ipForm) {
                validation(ipForm, rule);

                ipForm.addEventListener("focus", () => {
                    const ipErr = ipForm.nextElementSibling;
                    console.log("forcus");
                    ipErr.textContent = "";
                });
            }
        });
    }
};

Authorization.isRequired = function (select, mes) {
    return {
        ele: select,
        rule: function (val) {
            return val.trim() ? null : mes || "Filed is Required!!";
        },
    };
};

Authorization.minLength = function (select, min = 6, mes) {
    return {
        ele: select,
        rule: (val) => {
            return val.lenght >= min
                ? null
                : mes || `Minximum of password is ${min} charactors`;
        },
    };
};

Authorization.compare = function (select, tar) {
    return {
        ele: select,
        rule: function (val) {
            const text = typeof tar === "function" ? tar() : tar;
            return val === text ? null : "Không khớp vui lòng kiểm tra lại";
        },
    };
};

export default Authorization;
