import axios from "axios";
import { ALL_CART_FAIL } from "../Cart/typeCart";
import {
    ALL_PRODUCT_REQUEST,
    ALL_PRODUCT_SUCCESS,
    ALL_PRODUCT_FAIL,
    SINGLE_PRODUCT_REQUEST,
    SINGLE_PRODUCT_SUCCESS,
    SINGLE_PRODUCT_FAIL,
    CLEAR_ERROR,
    SERVER,
    PAGE_SEARCH_SUCCESS,
    PAGE_SEARCH_REQUEST,
} from "./typeProduct";

export const getCategory = () => async (dispatch) => {
    dispatch({ type: ALL_PRODUCT_REQUEST });
    const data = await axios.get(`${SERVER}/api/categories`).catch((err) =>
        dispatch({
            type: ALL_PRODUCT_FAIL,
            payload: {
                error: err.response.data.message,
            },
        })
    );
};

export const pageSearch = (query) => async (dispatch) => {
    try {
        dispatch({ type: PAGE_SEARCH_REQUEST });

        const { data } = await axios.get(`${SERVER}/api/page/search${query}`);
        dispatch({
            type: PAGE_SEARCH_SUCCESS,
            payload: {
                pros: data.pros,
            },
        });
    } catch (err) {
        dispatch({
            type: ALL_PRODUCT_FAIL,
            payload: {
                error: err.response.data.message,
            },
        });
    }
};

export const searchProduct = (name) => async (dispatch) => {
    try {
        const { data } = await axios.get(
            `${SERVER}/api/products/search?q=${name}`
        );

        return data;
    } catch (err) {
        dispatch({
            type: ALL_PRODUCT_FAIL,
            payload: err.response.data.message,
        });
    }
};

export const getAllProduct = () => async (dispatch) => {
    try {
        dispatch({
            type: ALL_PRODUCT_REQUEST,
        });
        const { data } = await axios.get(`${SERVER}/api/products`);
        dispatch({
            type: ALL_PRODUCT_SUCCESS,
            payload: {
                pros: data.pros,
            },
        });
    } catch (err) {
        dispatch({
            type: ALL_PRODUCT_FAIL,
            payload: err.response.data.message,
        });
    }
};
export const getProductById = (id) => async (dispatch) => {
    try {
        dispatch({ type: SINGLE_PRODUCT_REQUEST });
        const { data } = await axios.get(`${SERVER}/api/products/${id}`);

        dispatch({
            type: SINGLE_PRODUCT_SUCCESS,
            payload: {
                isLoad: false,
                pro: data.pro,
            },
        });
    } catch (err) {
        dispatch({
            type: SINGLE_PRODUCT_FAIL,
            payload: err.response.data.message || "Opps! Have a error",
        });
    }
};

export const clearError = () => async (dispatch) => {
    dispatch({
        type: CLEAR_ERROR,
    });
};
