import {
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,
    USER_AUTH_FAIL,
    USER_SIGN_UP_REQUEST,
    USER_SIGN_UP_SUCCESS,
    USER_VERIFY,
    ACCESSTOKEN,
} from "./typeAuth";
import { useEffect } from "react";
import axios from "axios";
import { SERVER } from "../../component/MainPages/helpers/key";
import setHeaderDefault from "../../component/MainPages/helpers/SetHeader";
import { useDispatch } from "react-redux";

export const userLogin = (LoginFormData) => async (dispatch) => {
    try {
        dispatch({
            type: USER_LOGIN_REQUEST,
        });
        const { data } = await axios.post(
            `${SERVER}/user/login`,
            LoginFormData
        );

        if (data.success) {
            localStorage.setItem(ACCESSTOKEN, data.accessToken);
        }

        // await loadUser();

        dispatch({
            type: USER_LOGIN_SUCCESS,
        });

        return data;
    } catch (err) {
        dispatch({
            type: USER_AUTH_FAIL,
            payload: {
                isLoad: false,
                error: err.response.data.message || "Login is fail",
            },
        });
    }
};
