const AuthReducer = (state, action) => {
    const { type, payload } = action;

    const { isAuth, user } = payload;

    switch (type) {
        case "SET_AUTH":
            return { ...state, isLoad: false, isAuth, user };

        default:
            return state;
    }
};

export default AuthReducer;
