import {
    ALL_CART_REQUEST,
    ALL_CART_SUCCESS,
    ALL_CART_FAIL,
    POST_CART,
    POST_CART_SUCCESS,
    ADD_TO_CART,
} from "./typeCart";

export const cartReduce = (state = { cart: [] }, action) => {
    const { type, payload } = action;

    switch (type) {
        case POST_CART:
            return {
                ...state,
                isLoad: true,
            };
        case POST_CART_SUCCESS:
            return {
                isLoad: false,
                cart: payload.cart,
            };
        case ALL_CART_REQUEST:
            return {
                ...state,
                isLoad: true,
            };
        case ALL_CART_SUCCESS:
            return {
                isLoad: false,
                cart: payload.cart,
            };
        case ALL_CART_FAIL:
            return {
                ...state,
                isLoad: false,
                error: payload.error,
            };
        case ADD_TO_CART:
            alert("add cart");

            return {
                ...state,
                cart: [...state.cart, payload.newItem],
            };
        default:
            return state;
    }
};
