import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import ProductItem from "../utils/ProductItem/ProductItem";
import { pageSearch } from "../../../Redux/ProductRedux/actionProduct";
import { useLocation } from "react-router-dom";
import { v4 as uuid4 } from "uuid";
const PageSearch = () => {
    const { search } = useLocation();
    const { isLoad, error, products } = useSelector((state) => state.products);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(pageSearch(search));
    }, [search]);

    return (
        <div className="grid wide">
            <div className="row bg-light my-3 py-2">
                <ul className="list-search d-flex align-items-center">
                    <li className="list-item-search">Sắp xếp theo</li>
                    <li className="list-item-search">Liên Quan</li>
                    <li className="list-item-search">Mới nhất</li>
                    <li className="list-item-search">Bán Chạy</li>
                </ul>
            </div>
            <div className="row container-mid">
                {products.map((item) => (
                    <div key={uuid4()} className="col mid-product l-2-5">
                        <ProductItem item={item} />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default PageSearch;
