import React, { useReducer, useEffect, useState } from "react";
import authReducer from "./authReducer";
import axios from "axios";
import { URL_SERVER, ACCESSTOKEN } from "../MainPages/helpers/key";
import setHeaderDefault from "../MainPages/helpers/SetHeader";
import Imgs from "../MainPages/helpers/Imgs";
import products from "../MainPages/helpers/products";

export const userContext = React.createContext();

const UserProvider = ({ children }) => {
    const [userState, dispatch] = useReducer(authReducer, {
        isLoad: true, //
        isAuth: false, //
        userId: null,
        userInfo: null,
        //
    });
    const [cart, setCart] = useState([]);
    const [imgState, setImgState] = useState(Imgs);
    const [proState, setProState] = useState(products);

    // const history = useHistory();
    const signUp = async (DataForm) => {
        try {
            const response = await axios.post(
                `${URL_SERVER}/register`,
                DataForm
            );
            return response.data;
        } catch (err) {
            return err.response.data
                ? err.response.data
                : {
                      sucess: false, //
                      msg: "Internal Server Error",
                  };
        }
    };

    const LoginForm = async (DataForm) => {
        try {
            const response = await axios.post(`${URL_SERVER}/login`, DataForm);

            if (response.data.success) {
                localStorage.setItem(ACCESSTOKEN, response.data.accessToken);
            }
            loadUser();
            return response.data;
        } catch (err) {
            return err.response.data
                ? err.response.data
                : { sucess: false, msg: "Internal server error" };
        }
    };

    const handleLogOut = () => {
        localStorage.removeItem(ACCESSTOKEN);
        setHeaderDefault(null);
        dispatch({
            type: "SET_AUTH",
            payload: {
                isAuth: false,
                userId: null,
            },
        });
    };

    const sendProfileForm = async (data) => {
        try {
            const response = await axios.post(
                `${URL_SERVER}/save/profile`,
                data
            );
            return response.data;
        } catch (err) {
            return err.response.data
                ? err.response.data
                : { sucess: false, msg: "Internal server error" };
        }
    };

    const loadUser = async () => {
        if (localStorage[ACCESSTOKEN])
            setHeaderDefault(localStorage[ACCESSTOKEN]);
        try {
            const res = await axios.get(`${URL_SERVER}/verify`);
            if (res.data.success) {
                dispatch({
                    type: "SET_AUTH",
                    payload: {
                        isAuth: true,
                        userId: res.data.id,
                        userInfo: res.data.user,
                    },
                });
            }
        } catch (err) {
            localStorage.removeItem(ACCESSTOKEN);
            setHeaderDefault(null);
            dispatch({
                type: "SET_AUTH", //
                payload: {
                    isAuth: false, //
                    userId: null,
                },
            });
        }
    };

    useEffect(() => loadUser(), []);

    const userContextData = {
        signUp,
        LoginForm,
        handleLogOut,
        userState,
        dispatch,
        sendProfileForm,
        imgData: [imgState, setImgState],
        ProductCart: { cart, setCart },
        pros: { proState, setProState },
    };
    return (
        <>
            <userContext.Provider value={userContextData}>
                {children}
            </userContext.Provider>
        </>
    );
};

export default UserProvider;
