import {
    ALL_CART_REQUEST,
    ALL_CART_SUCCESS,
    ALL_CART_FAIL,
    POST_CART,
    POST_CART_SUCCESS,
    ADD_TO_CART,
} from "./typeCart";
import axios from "axios";
import { SERVER } from "../../component/MainPages/helpers/key";

export const AllCartRequest = () => async (dispatch) => {
    try {
        dispatch({
            type: ALL_CART_REQUEST,
        });
        const { data } = await axios.get(`${SERVER}/api/cart`);

        dispatch({
            type: ALL_CART_SUCCESS,
            payload: {
                cart: data.cart,
            },
        });
    } catch (err) {
        dispatch({
            type: ALL_CART_FAIL,
            payload: {
                error: err.response.data.message || "Get all cart is fail",
            },
        });
    }
};
export const postCart = (data) => async (dispatch) => {
    try {
        dispatch({
            type: POST_CART,
        });
        const { data } = await axios.post(`${SERVER}/api/cart/add`, data);

        dispatch({
            type: POST_CART_SUCCESS,
            payload: {
                cart: data.cart,
            },
        });
    } catch (err) {
        dispatch({
            type: ALL_CART_FAIL,
            payload: {
                error: err.response.data.message || "Get all cart is fail",
            },
        });
    }
};

export const AddToCart = (pro) => async (dispatch) => {
    dispatch({
        type: ADD_TO_CART,
        payload: {
            newItem: pro,
        },
    });
};
