import React, { useState, useContext } from "react";
import { Link } from "react-router-dom";
import bkg from "../../public/img/background.png";
import axios from "axios";
import { userContext } from "../Context/userProvider";

const Signup = () => {
    const { signUp } = useContext(userContext);

    const [signForm, setLoginForm] = useState({
        email: "",
        password: "",
        verify: "",
    });

    const msg = React.createRef();

    const { email, password, verify } = signForm;

    const onInputChange = (e) => {
        setLoginForm({ ...signForm, [e.target.name]: e.target.value });
    };

    const handleLoginForm = async (e) => {
        e.preventDefault();
        const { email, password } = signForm;
        const res = signUp({ email, password });
        if (res.sucess) return;
    };

    return (
        <>
            <div className="app-main">
                <div className="msg">
                    <i className="fa fa-check-circle" />
                    <span>Your Email is already exist</span>
                    <i className="fa fa-times"></i>
                </div>
                <img
                    src={bkg}
                    alt="background"
                    width="1200px"
                    className="app-bkg"
                />
                <div className="grid wide app-container">
                    <div className="app-signup l-o-7 m-o-4">
                        <form
                            action="#"
                            method="POST"
                            onSubmit={handleLoginForm}
                        >
                            <h1 className="app-title">
                                Sign Up{" "}
                                <Link to="/login" className="or-login">
                                    or login
                                </Link>
                            </h1>
                            <div className="sign-other-way">
                                <div className="other-way-btn btn-gg">
                                    <i className="fab fa-google-plus"></i>
                                    Google+
                                </div>
                                <div className="other-way-btn btn-fb">
                                    <i className="fab fa-facebook"></i> Facebook
                                </div>
                            </div>
                            <div className="input-wrap">
                                <label className="form-lable" htmlFor="email">
                                    Email
                                </label>
                                <input
                                    name="email"
                                    type="text"
                                    className="form-input"
                                    id="email"
                                    placeholder="Nhập email"
                                    value={email}
                                    onChange={onInputChange}
                                />
                                <span className="email-err form-err"></span>
                            </div>

                            <div className="input-wrap">
                                <label
                                    className="form-lable"
                                    htmlFor="password"
                                >
                                    Nhập mật khẩu
                                </label>
                                <input
                                    name="password"
                                    type="password"
                                    className="form-input"
                                    id="password"
                                    placeholder="Nhập mật khẩu"
                                    value={password}
                                    onChange={onInputChange}
                                />
                                <span className="password-err form-err"></span>
                            </div>

                            <div className="input-wrap">
                                <label className="form-lable" htmlFor="verify">
                                    Nhập lại mật khẩu
                                </label>
                                <input
                                    name="verify"
                                    type="password"
                                    className="form-input"
                                    id="verify"
                                    placeholder="Nhập mật khẩu"
                                    value={verify}
                                    onChange={onInputChange}
                                />
                                <span className="verify-err form-err"></span>
                            </div>
                            <button className="btn btn_ful">Đăng Ký</button>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Signup;
