import React, { useContext, useEffect, useState } from "react";
import { userContext } from "../../Context/userProvider";
import { Link } from "react-router-dom";
import { v4 as uuidv4 } from "uuid";

const Cart = () => {
    const {
        ProductCart: { cart, setCart },
    } = useContext(userContext);

    const [check, setCheck] = useState(0);
    const listInput = React.createRef();

    useEffect(() => {
        const main = listInput.current;
        var checkControl = main.querySelectorAll(".check-all");
        var tar = main.querySelectorAll("input[name]");
        var itemCheck = main.querySelectorAll('input[name="item-check"]');

        for (let i = 0; i < checkControl.length; i++) {
            checkControl[i].addEventListener("change", checkAll);
        }

        const test = () => {
            let index = 0;
            for (let i = 0; i < tar.length; i++) {
                tar[i].checked = true && index++;
            }
            setCheck(index);
        };

        function checkAll(ele) {
            const isCheck = ele.currentTarget.checked;
            for (let i = 0; i < tar.length; i++) {
                tar[i].checked = isCheck;
            }
            test();
        }

        for (let i = 0; i < itemCheck.length; i++) {
            itemCheck[i].addEventListener("change", () => {
                let isFull =
                    itemCheck.length ===
                    main.querySelectorAll('input[name="item-check"]:checked')
                        .length;

                for (let i = 0; i < checkControl.length; i++) {
                    checkControl[i].checked = isFull;
                }
                test();
            });
        }
    }, []);

    return (
        <>
            <div className="app grid wide bg-light">
                <div className="card-pro" ref={listInput}>
                    <nav className="nav d-flex my-4 px-4 py-2 bg-white border rounded">
                        <li className="nav-item d-flex align-items-center flex-fill">
                            <input
                                className="form-input-check check-all"
                                type="checkbox"
                                name="all"
                                id="all"
                            />
                            <label
                                className="nav-link ps-2 text-dark-60"
                                htmlFor="all"
                            >
                                Sản phẩm
                            </label>
                        </li>
                        <li className="nav-item w-20 ms-auto l-2">
                            <Link
                                to="#"
                                className="nav-link disabled ps-0"
                                href="#"
                                tabIndex="-1"
                                aria-disabled="true"
                            >
                                Đơn Giá
                            </Link>
                        </li>
                        <li className="nav-item l-2">
                            <Link
                                to="#"
                                className="nav-link disabled ps-0"
                                href="#"
                                tabIndex="-1"
                                aria-disabled="true"
                            >
                                Số Lượng
                            </Link>
                        </li>
                        <li className="nav-item l-2">
                            <Link
                                to="#"
                                className="nav-link disabled ps-0"
                                href="#"
                                tabIndex="-1"
                                aria-disabled="true"
                            >
                                Số Tiền
                            </Link>
                        </li>
                    </nav>
                    <div className="card-list ">
                        <div className="card-list-mark">
                            {cart.length === 0 && (
                                <h1 className="text-center text-muted mb-4">
                                    Không có sản phẩm
                                </h1>
                            )}
                            {cart.length > 0 &&
                                cart.map((item) => (
                                    <div
                                        key={uuidv4()}
                                        className="px-4 d-flex card-body bg-white border border-bottom-0 rounded"
                                    >
                                        <div className="product-desc d-flex align-items-center flex-fill">
                                            <input
                                                className="form-check-input"
                                                type="checkbox"
                                                name="item-check"
                                            />
                                            <div
                                                className="div l-1 img-pf mx-2"
                                                style={{
                                                    paddingBottom: " 9%",
                                                    background: ` url(${item.Img})`,
                                                }}
                                            ></div>
                                            <span className="product-text align-self-start text-muted l-5 text-break lh-2 text-ellipsis">
                                                {item.Name}
                                            </span>
                                        </div>
                                        <div className="product-prize l-2 d-flex mt-3">
                                            <small className="text-decoration-line-through text-muted me-2 ">
                                                {item.Prize}
                                                <span className="under">đ</span>
                                            </small>
                                            <p className="prize-now ">
                                                {parseInt(item.Prize) *
                                                    (1 -
                                                        parseInt(item.Promo) /
                                                            100) *
                                                    item.num}
                                                <span className="under">đ</span>
                                            </p>
                                        </div>
                                        <div className="product-sell-num l-2 mt-2">
                                            <div className="pagination-sm d-flex">
                                                <div
                                                    className="page-link text-muted cursor-pointer"
                                                    // onClick={() => reduction(item.id)}
                                                >
                                                    <i className="fas fa-minus"></i>
                                                </div>
                                                <div
                                                    className="page-item disabled"
                                                    disabled
                                                >
                                                    <span className="page-link">
                                                        {item.num}
                                                    </span>
                                                </div>
                                                <div
                                                    className="page-link text-muted cursor-pointer"
                                                    // onClick={() => increase(item.id)}
                                                >
                                                    <i className=" fas fa-plus"></i>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="product-money l-2 mt-3 d-flex justify-content-between">
                                            <span className="text-warning">
                                                {parseInt(item.Prize) *
                                                    (1 -
                                                        parseInt(item.Promo) /
                                                            100) *
                                                    item.num}
                                                <span className="under">đ</span>
                                            </span>
                                            <span
                                                className="me-5 cursor-pointer cart-remove-trash"
                                                // onClick={() => removeProduct}
                                            >
                                                <i className="far fa-trash-alt "></i>
                                            </span>
                                        </div>
                                    </div>
                                ))}
                        </div>

                        <div className="px-4 card-footer d-flex align-items-center">
                            <div className="footer-check">
                                <input
                                    type="checkbox"
                                    className="form-check-input me-2 check-all"
                                    name="cad"
                                    id="all-select"
                                />
                                <label htmlFor="all-select">
                                    Chọn tất cả ({check})
                                </label>

                                {check === 0 && (
                                    <span className="text-muted ms-1">
                                        (Xoá)
                                    </span>
                                )}
                                {check > 0 && (
                                    <Link
                                        to="#"
                                        className="ms-1 text-decoration-none"
                                        href="#"
                                    >
                                        (Xoá)
                                    </Link>
                                )}
                            </div>
                            <div className="footer-text ms-auto l-4 text-center d-flex align-items-center">
                                <span className="ms-auto fs-6 text-dark-2">
                                    Tổng thanh toán ({check} sp):
                                </span>
                                <span className="text-warning fs-4 mx-3 mb-1">
                                    <span className="under">đ</span>
                                </span>
                            </div>
                            <button className="btn btn-info text-white">
                                Mua Hàng
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Cart;
