const Authorization = ({ formName, rules, state }) => {
    const form = document.forms[formName];
    if (form) {
        rules.forEach(({ ele, rule }) => {
            const valInput = state[ele];
            const ipForm = form.querySelector(`#${ele}`);
            const ipErr = ipForm.nextElementSibling;

            const isVaid = rule(valInput);

            // val ? false : true

            if (isVaid) {
                ipErr.textContent = isVaid;
            }

            ipForm.addEventListener("focus", () => {
                console.log("forcus");
                ipErr.textContent = "";
            });
        });
    }
};

Authorization.isRequired = function (select, mes) {
    return {
        ele: select,
        rule: function (val) {
            return val.trim() ? null : mes || "Filed is Required!!";
        },
    };
};

Authorization.minLength = function (select, min = 6, mes) {
    return {
        ele: select,
        rule: (val) => {
            return val.lenght >= min
                ? null
                : mes || `Minximum of password is ${min} charactors`;
        },
    };
};

Authorization.compare = function (select, tar) {
    const text = document.querySelector(`#${tar}`).value;
    console.log(text);
    return {
        ele: select,
        rule: function (val) {
            return val === tar ? null : "Không khớp vui lòng kiểm tra lại";
        },
    };
};

export default Authorization;
