import React, { useContext, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import bkg from "../../public/img/background.png";
import { userContext } from "../Context/userProvider";
import ErrorForm from "../Form/ErrorForm";

const Authorization = ({ formName, rules, loginState }) => {
    const form = document.forms[formName];

    if (form) {
        rules.forEach(({ ele, rule }) => {
            const valInput = loginState[ele];
            const ipErr = form.querySelector(".email-err");
            const ipForm = form.querySelector(`#${ele}`);

            const isVaid = rule(valInput);

            // val ? false : true

            if (isVaid) {
                ipErr.textContent = isVaid;
            }

            ipForm.addEventListener("focus", () => {
                console.log("forcus");
                ipErr.textContent = "";
            });
        });
    }
};

Authorization.isRequired = function (select) {
    return {
        ele: select,
        rule: function (val) {
            return val.trim() ? null : "Filed is Required!!";
        },
    };
};

Authorization.minLength = function (select, min = 6) {
    return {
        ele: select,
        rule: (val) => {
            return val.lenght >= min
                ? null
                : `Minximum of password is ${min} charactors`;
        },
    };
};

const Login = () => {
    const [loginState, setLoginState] = useState({
        email: "",
        password: "",
    });
    const formRef = React.createRef();
    const [msg, setMsg] = useState(null);
    const history = useHistory();

    const { LoginForm } = useContext(userContext);

    const { email, password } = loginState;

    const onChangeInputForm = (e) => {
        setLoginState({ ...loginState, [e.target.name]: e.target.value });
    };

    const postFormLogin = async () => {
        const res = await LoginForm(loginState); //

        console.log(res);

        if (res.success) {
            history.push("/");
        } else {
            setMsg([res.msg]); //
            setTimeout(() => setMsg(null), 6000);
        }
    };

    const onSumbmitFormLogin = async (e) => {
        e.preventDefault();

        Authorization({
            formName: "login",
            loginState,
            setLoginState,
            rules: [
                Authorization.isRequired("email"),
                Authorization.isRequired("password"),
            ],
        });
    };

    const RemoveItem = (i) => {
        const newMsg = msg.filter((item, index) => {
            return index !== i;
        });
        setMsg([...newMsg]);
    };

    return (
        <>
            <div className="app-main">
                <div className="msg-wrap">
                    <ErrorForm msg={msg} removeItem={RemoveItem} />
                </div>
                <img
                    src={bkg}
                    alt="background"
                    width="1200px"
                    className="app-bkg"
                />
                <div className="grid wide app-container">
                    <div className="app-signup l-o-7 m-o-4">
                        <form
                            name="login"
                            action="#"
                            method="POST"
                            onSubmit={onSumbmitFormLogin}
                            ref={formRef}
                        >
                            <h1 className="app-title">
                                Log In{" "}
                                <Link to="/signup" className="or-login">
                                    or Sign up
                                </Link>
                            </h1>
                            <div className="sign-other-way">
                                <div className="other-way-btn btn-gg">
                                    <i className="fab fa-google-plus"></i>
                                    &nbsp; Google+
                                </div>
                                <div className="other-way-btn btn-fb">
                                    <i className="fab fa-facebook"></i>{" "}
                                    &nbsp;Facebook
                                </div>
                            </div>
                            <div className="input-wrap">
                                <label className="form-lable" htmlFor="email">
                                    Email
                                </label>
                                <input
                                    type="text"
                                    name="email"
                                    className="form-input"
                                    id="email"
                                    placeholder="Nhập email"
                                    value={email}
                                    onChange={onChangeInputForm}
                                />
                                <span className="email-err form-err"></span>
                            </div>

                            <div className="input-wrap">
                                <label
                                    className="form-lable"
                                    htmlFor="password"
                                >
                                    Nhập mật khẩu
                                </label>
                                <input
                                    type="password"
                                    name="password"
                                    className="form-input"
                                    id="password"
                                    placeholder="Nhập mật khẩu"
                                    value={password}
                                    onChange={onChangeInputForm}
                                />
                                <span className="password-err form-err"></span>
                            </div>
                            <button className="btn btn_ful">Đăng Nhập</button>
                            <div className="plugin-login">
                                <div className="remember-account">
                                    <input type="checkbox" id="check" />
                                    <label
                                        htmlFor="check"
                                        className="form-label"
                                    >
                                        Remember me
                                    </label>
                                </div>
                                <a href="#" className="forgot-pass">
                                    Forget password??
                                </a>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Login;
