import React, { useState } from "react";
import axios from "axios";

const UpImage = () => {
    // const [fileInputSate, setFileInputState] = React.useState("");
    // const [selectedFile, setSelectedFile] = React.useState("");
    // const [previewSource, setPreviewSource] = React.useState();

    const [images, setImages] = useState(false);
    const [loading, setLoading] = useState(false);

    const handleFileInputChange = (e) => {
        try {
            const file = e.target.files[0];
            console.log(file);
            if (!file) return alert("File is not exist");
            if (file.size > 1024 * 1024) return alert("Size too large!!");
            if (file.type !== "image/jpeg" && file.type !== "image/png")
                return alert("File format is incorrect");
            let formData = new FormData();
            formData.append("file", file);
        } catch (err) {
            console.log(err);
        }

        // previewFile(file);
    };
    const handleSubmitFile = () => {};
    // const previewFile = (file) => {
    //     const reader = new FileReader();
    //     reader.readAsDataURL(file);
    //     reader.onloadend = () => {
    //         setPreviewSource(reader.result);
    //     };
    // };
    // const handleSubmitFile = (e) => {
    //     e.preventDefault();
    //     if (!previewSource) return;
    //     uploadImage(previewSource);
    // };

    // const uploadImage = async (base64EncodedImage) => {
    //     console.log(base64EncodedImage);
    //     try {
    //         await axios('http://localhost:5000/api/upload', )
    //     } catch (err) {
    //         console.log(err);
    //     }
    // };
    return (
        <>
            <h1 className="text-center">Upload</h1>
            <form className="form" onSubmit={handleSubmitFile}>
                <div className="form-group">
                    <label htmlFor="img">Img</label>
                    <input
                        type="file"
                        name="file"
                        value={images}
                        onChange={handleFileInputChange}
                    />
                </div>
                <button type="submit" className="btn btn-primary">
                    Submit
                </button>
            </form>
            {images && (
                <img
                    src={images}
                    alt="chosen"
                    style={{ height: "300px", width: "300px" }}
                />
            )}
        </>
    );
};

export default UpImage;
