import {
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,
    USER_AUTH_FAIL,
    USER_SIGN_UP_REQUEST,
    USER_SIGN_UP_SUCCESS,
    USER_VERIFY,
} from "./typeAuth";

const userState = {
    isAuth: false,
    user: null,
};

export const userAuth = (state = userState, action) => {
    const { type, payload } = action;

    switch (type) {
        case USER_LOGIN_REQUEST:
            return {
                ...state,
                isLoad: true,
            };
        case USER_LOGIN_SUCCESS:
            return {
                ...state,
                isLoad: false,
            };

        case USER_SIGN_UP_REQUEST:
            return {
                ...state,
                isLoad: true,
            };

        case USER_SIGN_UP_SUCCESS:
            return {
                ...state,
                isLoad: false,
            };

        case USER_AUTH_FAIL:
            return {
                ...state,
                isAuth: false,
                error: payload.error,
            };
        case USER_VERIFY:
            return {
                isLoad: false,
                isAuth: payload.isAuth,
                user: payload.user,
            };

        default:
            return state;
    }
};
