import React, { useEffect, useContext, useState } from "react";
import { userContext } from "../Context/userProvider";
// import ArowBackIosIcon from "@material-ui/icons/ArrowBackIos";
// import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
// import { ListAlt } from "@material-ui/icons";
const Carousel = () => {
    const { imgData } = useContext(userContext);
    const [imgState, setImgState] = imgData;
    console.log(imgState);
    // const [index, setIndex] = useState(1);
    let container = React.createRef();
    let slide = React.createRef();
    let index = 1;
    let slideId;

    useEffect(() => {
        let wrap = container.current;
        let listSlide = slide.current;
        let pre = document.querySelector(".pre");
        let next = document.querySelector(".next");
        let list = listSlide.querySelectorAll(".carousel-slide");
        // let slideWrap = container.querySelector(".carousel-inner");

        let firstSlide = list[0].cloneNode(true);
        let lastSlide = list[list.length - 1].cloneNode(true);

        firstSlide.id = "first-id";
        lastSlide.id = "last-id";

        listSlide.appendChild(firstSlide);
        listSlide.insertBefore(lastSlide, list[0]);
        // setImgState([imgState[imgState.length - 1], ...imgState, imgState[0]]);

        let WidthSlide = list[index].clientWidth;

        listSlide.style.transform = `translateX(-${WidthSlide}px)`;

        const running = (n) => {
            listSlide = slide.current;
            list = listSlide.querySelectorAll(".carousel-slide");

            if (index >= list.length - 1) return;
            index += n;
            listSlide.style.transform = `translateX(-${WidthSlide * index}px)`;
            listSlide.style.transition = ".8s";
        };

        const runSlide = (n) => {
            slideId = setInterval(() => {
                running(1);
            }, 2000);
        };

        listSlide.addEventListener("transitionend", () => {
            listSlide = slide.current;
            list = listSlide.querySelectorAll(".carousel-slide");
            if (list[index].id === firstSlide.id) {
                listSlide.style.transition = "none";
                index = 1;
                listSlide.style.transform = `translateX(-${
                    WidthSlide * index
                }px)`;
            }
        });

        wrap.addEventListener("mouseover", () => {
            clearInterval(slideId);
        });
        wrap.addEventListener("mouseleave", () => {
            runSlide(1);
        });

        next.addEventListener("click", () => {
            running(1);
        });
        pre.addEventListener("click", () => {
            running(-1);
        });

        runSlide();
    }, []);
    return (
        <div className="carousel" ref={container}>
            <div className="carousel-inner" ref={slide}>
                {imgState.map((item, index) => (
                    <div className="carousel-slide" key={index}>
                        <img src={item} alt="" />
                    </div>
                ))}
            </div>
            <div className="carousel-control">
                <span className="ctl pre"></span>
                <span className="ctl next"></span>
            </div>
        </div>
    );
};
export default Carousel;
