import React, { useState, useContext, useEffect } from "react";
import { Link } from "react-router-dom";
import bkg from "../../public/img/background.png";
import axios from "axios";
import { userContext } from "../Context/userProvider";
import { v4 as uuid4 } from "uuid";
import ErrorForm from "./ErrorForm";

const Signup = () => {
    const { signUp } = useContext(userContext);

    const [signForm, setLoginForm] = useState({
        email: "",
        password: "",
        verify: "",
        msg: [],
    });

    const appear = React.createRef();

    const { email, password, verify, msg } = signForm;

    const onInputChange = (e) => {
        setLoginForm({ ...signForm, [e.target.name]: e.target.value });
    };

    const handleLoginForm = async (e) => {
        e.preventDefault();
        const { email, password } = signForm;
        const res = await signUp({ email, password });
        console.log(res);
        if (res.success) setLoginForm({ ...signForm, msg: [res.msg] });
        else {
            const err = Object.values(res.errors).filter((item) => {
                return item !== "";
            });
            setLoginForm({ ...signForm, msg: [...err] });
        }
    };

    const RemoveItem = (i) => {
        const newMsg = msg.filter((item, index) => {
            return index !== i;
        });
        setLoginForm({ ...signForm, msg: newMsg });
    };

    return (
        <>
            <div className="app-main">
                <div className="msg-wrap">
                    {msg.map((item, index) => (
                        <ErrorForm
                            key={uuid4()}
                            item={item}
                            index={index}
                            remove={RemoveItem}
                        />
                    ))}
                </div>
                <img
                    src={bkg}
                    alt="background"
                    width="1200px"
                    className="app-bkg"
                />
                <div className="grid wide app-container">
                    <div className="app-signup l-o-7 m-o-4">
                        <form
                            action="#"
                            method="POST"
                            onSubmit={handleLoginForm}
                        >
                            <h1 className="app-title">
                                Sign Up{" "}
                                <Link to="/login" className="or-login">
                                    or login
                                </Link>
                            </h1>
                            <div className="sign-other-way">
                                <div className="other-way-btn btn-gg">
                                    <i className="fab fa-google-plus"></i>
                                    Google+
                                </div>
                                <div className="other-way-btn btn-fb">
                                    <i className="fab fa-facebook"></i> Facebook
                                </div>
                            </div>
                            <div className="input-wrap">
                                <label className="form-lable" htmlFor="email">
                                    Email
                                </label>
                                <input
                                    name="email"
                                    type="text"
                                    className="form-input"
                                    id="email"
                                    placeholder="Nhập email"
                                    value={email}
                                    onChange={onInputChange}
                                />
                                <span className="email-err form-err"></span>
                            </div>

                            <div className="input-wrap">
                                <label
                                    className="form-lable"
                                    htmlFor="password"
                                >
                                    Nhập mật khẩu
                                </label>
                                <input
                                    name="password"
                                    type="password"
                                    className="form-input"
                                    id="password"
                                    placeholder="Nhập mật khẩu"
                                    value={password}
                                    onChange={onInputChange}
                                />
                                <span className="password-err form-err"></span>
                            </div>

                            <div className="input-wrap">
                                <label className="form-lable" htmlFor="verify">
                                    Nhập lại mật khẩu
                                </label>
                                <input
                                    name="verify"
                                    type="password"
                                    className="form-input"
                                    id="verify"
                                    placeholder="Nhập mật khẩu"
                                    value={verify}
                                    onChange={onInputChange}
                                />
                                <span className="verify-err form-err"></span>
                            </div>
                            <button className="btn btn_ful">Đăng Ký</button>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Signup;
