import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import ProductItem from "../utils/ProductItem/ProductItem";
import { pageSearch } from "../../../Redux/ProductRedux/actionProduct";
import { useLocation } from "react-router-dom";
import { v4 as uuid4 } from "uuid";
const PageSearch = () => {
    const { search } = useLocation();
    const { isLoad, error, products } = useSelector((state) => state.products);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(pageSearch(search));
    }, [search]);

    return (
        <div className="grid wide">
            <div className="row bg-light my-3 py-2">
                <ul class="list-search d-flex align-items-center mb-0 ps-0">
                    <li class="list-item-search px-3 btn-sm">Sắp xếp theo</li>
                    <li class="list-item-search px-3 btn btn-sm  me-2 btn btn-active">
                        Liên Quan
                    </li>
                    <li class="list-item-search px-3 btn btn-sm btn-white me-2">
                        Mới nhất
                    </li>
                    <li class="list-item-search px-3 btn btn-sm btn-white me-2">
                        Bán Chạy
                    </li>
                    <li className="list-item-search">
                        <div className="search-item-search-text">
                            <span className="search-text">Giá</span>
                            <span className="search-text-icon">
                                <i className="far fa-arrow-down"></i>
                            </span>
                        </div>
                    </li>
                </ul>
            </div>
            <div className="row container-mid">
                {products.map((item) => (
                    <div key={uuid4()} className="col mid-product l-2-5">
                        <ProductItem item={item} />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default PageSearch;
