import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import "./upImage.css";
import { userContext } from "../../../Context/userProvider";

const UpImage = () => {
    // const [fileInputSate, setFileInputState] = React.useState("");
    // const [selectedFile, setSelectedFile] = React.useState("");
    // const [previewSource, setPreviewSource] = React.useState();

    const {
        api: { getCategory },
    } = useContext(userContext);
    const [images, setImages] = useState(false);
    const [loading, setLoading] = useState(false);

    useEffect(async () => {
        let data = await getCategory();
        console.log(data);
    });

    const handleFileInputChange = async (e) => {
        // if (images) deleteImage(images.public_id);
        try {
            const file = e.target.files[0];
            console.log(file);
            if (!file) return alert("File is not exist");
            if (file.size > 1024 * 1024) return alert("Size too large!!");
            if (file.type !== "image/jpeg" && file.type !== "image/png")
                return alert("File format is incorrect");
            let formData = new FormData();
            formData.append("file", file);
            setLoading(true);
            const res = await axios.post(
                "http://localhost:5000/service/upload",
                formData,
                {
                    headers: {
                        "content-type": "multipart/form-data",
                    },
                }
            );
            setLoading(false);
            setImages(res.data);
            console.log(res);
        } catch (err) {
            console.log(err);
        }

        // previewFile(file);
    };
    const deleteImage = async () => {
        setLoading(true);
        const res = await axios.post("http://localhost:5000/service/destroy", {
            public_id: images.public_id,
        });
        console.log(res);
        setLoading(false);
        setImages(false);
    };

    let imgStyle = images ? { display: "block" } : { display: "none" };

    // const previewFile = (file) => {
    //     const reader = new FileReader();
    //     reader.readAsDataURL(file);
    //     reader.onloadend = () => {
    //         setPreviewSource(reader.result);
    //     };
    // };
    // const handleSubmitFile = (e) => {
    //     e.preventDefault();
    //     if (!previewSource) return;
    //     uploadImage(previewSource);
    // };

    // const uploadImage = async (base64EncodedImage) => {
    //     console.log(base64EncodedImage);
    //     try {
    //         await axios('http://localhost:5000/api/upload', )
    //     } catch (err) {
    //         console.log(err);
    //     }
    // };
    return (
        <>
            <h1 className="text-center">Upload</h1>
            <div className="main d-flex">
                <div className="upimg l-4 px-4 flex-fill">
                    <div className="form-group img-upload h-100 position-relative">
                        <label
                            htmlFor="img"
                            className={
                                images
                                    ? "d-none"
                                    : `l-12
                            w-100 h-100 border 
                            border-info d-flex 
                            justify-content-center 
                            align-items-center 
                            cursor-pointer`
                            }
                        >
                            <i className="fas fa-plus fs-1 text-warning"></i>
                        </label>
                        <input
                            className="d-none"
                            type="file"
                            name="file"
                            id="img"
                            onChange={handleFileInputChange}
                        />
                        <img
                            style={imgStyle}
                            src={images ? images.url : ""}
                            alt="choosen"
                        />
                        {images && (
                            <i
                                className="far fa-times-circle img-close cursor-pointer text-danger"
                                onClick={deleteImage}
                            ></i>
                        )}
                    </div>
                </div>
                <div className="form-wrap l-8 ps-2 pe-4">
                    <form name="form-products" className="form-products">
                        <div className="form-group form-control-product">
                            <label htmlFor="name">Product Name</label>
                            <input
                                type="text"
                                name="name"
                                id="name"
                                className="form-control"
                            />
                        </div>
                        <div className="form-group form-control-product">
                            <label htmlFor="prize">Product Prize</label>
                            <input
                                type="text"
                                name="prize"
                                id="prize"
                                className="form-control"
                            />
                        </div>
                        <div className="form-group form-control-product">
                            <label htmlFor="promo">Product Promotion</label>
                            <input
                                type="text"
                                name="promo"
                                id="promo"
                                className="form-control"
                            />
                        </div>
                        <div className="form-group form-control-product">
                            <label htmlFor="status">Product Status</label>
                            <input
                                type="text"
                                name="status"
                                id="status"
                                className="form-control"
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="sel">Product Category</label>
                            <select className="form-select" id="sel">
                                <option value="1">Quần áo</option>
                                <option value="2">Giày dép</option>
                                <option value="3">Phụ kiện</option>
                            </select>
                        </div>
                    </form>
                </div>
            </div>
        </>
    );
};

export default UpImage;
