import {
    ALL_CART_REQUEST,
    ALL_CART_SUCCESS,
    ALL_CART_FAIL,
    POST_CART,
    POST_CART_SUCCESS,
    ADD_TO_CART,
    SERVER,
} from "./typeCart";
import axios from "axios";

export const AllCartRequest = () => async (dispatch) => {
    try {
        dispatch({
            type: ALL_CART_REQUEST,
        });
        const { data } = await axios.get(`${SERVER}/api/cart`);

        dispatch({
            type: ALL_CART_SUCCESS,
            payload: {
                cart: data.cart,
            },
        });
    } catch (err) {
        dispatch({
            type: ALL_CART_FAIL,
            payload: {
                error: err.response.data.message || "Get all cart is fail",
            },
        });
    }
};
export const postCart = (cartState) => async (dispatch) => {
    console.log(cartState);
    try {
        dispatch({
            type: POST_CART,
        });
        const { data } = await axios.post(`${SERVER}/api/cart/add`, cartState);

        dispatch({
            type: POST_CART_SUCCESS,
            payload: {
                cart: data.cart,
            },
        });
    } catch (err) {
        dispatch({
            type: ALL_CART_FAIL,
            payload: {
                error: err.response.data.message || "Get all cart is fail",
            },
        });
    }
};

export const AddToCart = (pro, num) => async (dispatch, getState) => {
    const state = getState();

    try {
        dispatch({
            type: ADD_TO_CART,
            payload: {
                newItem: { ...pro, num },
            },
        });
        console.log(state.cartReducer);
        await axios.post(`${SERVER}/api/cart/add`, [
            ...state.cartReducer.cart,
            { ...pro, num },
        ]);
    } catch (err) {
        console.log(err);
        dispatch({
            type: ALL_CART_FAIL,
            payload: {
                error: err.response.data.message || "Post cart fail",
            },
        });
    }
};
