import React, { useState, useEffect } from "react";
import axios from "axios";
import { SERVER } from "../../component/MainPages/helpers/key";

const GetProduct = async () => {
    const [proState, setProState] = useState([]);

    const get = async () => {
        const response = await axios.get(`${SERVER}/api/products`);
        setProState(response.data.pros);
    };
    console.log(proState);

    useEffect(() => {
        get();
    }, []);

    return {
        pros: [proState, setProState],
    };
};

export default GetProduct;
