import {
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,
    USER_AUTH_FAIL,
    USER_SIGN_UP_REQUEST,
    USER_SIGN_UP_SUCCESS,
    USER_VERIFY,
} from "./typeAuth";

export const userAuth = (state = { isAuth: false, user: null }, action) => {
    const { type, payload } = action;
    const { isAuth, user } = payload;

    switch (type) {
        case USER_LOGIN_REQUEST:
            return {
                ...state,
                isLoad: true,
            };
        case USER_LOGIN_SUCCESS:
            return {
                ...state,
                isLoad: false,
            };

        case USER_SIGN_UP_REQUEST:
            return {
                ...state,
                isLoad: true,
            };

        case USER_SIGN_UP_SUCCESS:
            return {
                ...state,
                isLoad: false,
            };

        case USER_AUTH_FAIL:
            return {
                ...state,
                isAuth: false,
                error: payload.error,
            };
        case USER_VERIFY:
            return {
                isLoad: false,
                isAuth,
                user,
            };

        default:
            return state;
    }
};
