import React, { useEffect } from "react";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import Container from "../Views/Container";
import Detail from "./DetailProduct/Detail";
import Cart from "./Cart/Cart";
import Loading from "./utils/Loading/Loading";
import Payment from "./PayMent/Payment";
import { useDispatch } from "react-redux";
import axios from "axios";
import setHeaderDefault from "./helpers/SetHeader";
import { ACCESSTOKEN } from "./helpers/key";

const Page = ({ component }) => {
    const dispatch = useDispatch();

    const loadUser = async () => {
        if (localStorage[ACCESSTOKEN]) {
            setHeaderDefault(localStorage[ACCESSTOKEN]);
        }

        try {
            const { data } = await axios.get(`${SERVER}/user/info`);

            if (data.success) {
                dispatch({
                    type: USER_VERIFY,
                    payload: {
                        isAuth: true,
                        user: data.user,
                    },
                });
            }
        } catch (err) {
            localStorage.removeItem(ACCESSTOKEN);
            setHeaderDefault(null);
            dispatch({
                type: USER_VERIFY, //
                payload: {
                    isAuth: false, //
                    user: null,
                },
            });
        }
    };
    // const param = useParams();
    // console.log(param);
    return (
        <div className="app">
            <Header />
            {component === "home" && <Container />}
            {component === "detail" && <Detail />}
            {component === "cart" && <Cart />}
            {component === "loading" && <Loading />}
            {component === "payment" && <Payment />}
            <Footer />
        </div>
    );
};

export default Page;
