import {
    ALL_CART_REQUEST,
    ALL_CART_SUCCESS,
    ALL_CART_FAIL,
    POST_CART,
    POST_CART_SUCCESS,
    ADD_TO_CART,
    SERVER,
    DELETE_FROM_CART,
} from "./typeCart";
import axios from "axios";

export const AllCartRequest = () => async (dispatch) => {
    try {
        dispatch({
            type: ALL_CART_REQUEST,
        });
        const { data } = await axios.get(`${SERVER}/api/cart`);
        // console.log(data);
        dispatch({
            type: ALL_CART_SUCCESS,
            payload: {
                cart: data.cart,
            },
        });
    } catch (err) {
        dispatch({
            type: ALL_CART_FAIL,
            payload: {
                error: err.response.data.message || "Get all cart is fail",
            },
        });
    }
};
export const postCart = (cartState) => async (dispatch) => {
    try {
        dispatch({
            type: POST_CART,
        });
        const { data } = await axios.post(`${SERVER}/api/cart/add`, cartState);

        dispatch({
            type: POST_CART_SUCCESS,
            payload: {
                cart: data.cart,
            },
        });
    } catch (err) {
        dispatch({
            type: ALL_CART_FAIL,
            payload: {
                error: err.response.data.message || "Get all cart is fail",
            },
        });
    }
};

export const AddToCart = (pro, num) => async (dispatch, getState) => {
    const state = getState();
    const { cart } = state.cartReducer;
    try {
        await dispatch({
            type: ADD_TO_CART,
            payload: {
                newItem: { ...pro, num },
            },
        });
        await axios.post(`${SERVER}/api/cart/add`, [...cart, { ...pro, num }]);
    } catch (err) {
        dispatch({
            type: ALL_CART_FAIL,
            payload: {
                error: err.response.data.message || "Post cart fail",
            },
        });
    }
};
export const SingleDeleteCart = (numId) => async (dispatch, getState) => {
    const state = getState();
    const { cart } = state.cartReducer;

    cart.forEach((item, index) => {
        if (item._id === numId) cart.splice(index, 1);
    });
    console.log(cart);

    try {
        dispatch({
            type: POST_CART,
        });

        await axios.post(`${SERVER}/api/cart/add`, cart);

        dispatch({
            type: DELETE_FROM_CART,
            payload: {
                cart: cart,
            },
        });
    } catch (err) {
        dispatch({
            type: ALL_CART_FAIL,
            payload: {
                error: err.response.data.message || "Remove product fail",
            },
        });
    }
};
