import React, { useRef, useEffect, useContext } from "react";
import { userContext } from "../../../Context/userProvider";
import handleNum from "../../helpers/handleNum";

const Slide = ({ title }) => {
    const main = useRef();
    let x = 0;
    const {
        pros: { proState },
    } = useContext(userContext);
    console.log(proState);
    useEffect(() => {});

    const onLikeProduct = (e) => {
        e.currentTarget.classList.toggle("product-mark-liked");
    };

    const controlSlide = (n) => {
        const slide = main.current;
        let slideItemWidth = (slide.clientWidth * 2) / 5;
        console.log(slideItemWidth);
        // x += n;
        slide.scrollLeft += 100;
    };

    return (
        <>
            <h3 className="prod-outsd-title fs-5">{title}</h3>
            <div className="pro-outsd-slide-control position-relative">
                <div className="pro-outsd-control">
                    <i
                        className="
                                pro-outsd-icon pro-outsd-icon-left
                                fas
                                fa-arrow-left
                            "
                        onClick={() => controlSlide(1)}
                    ></i>
                    <i
                        className="
                                pro-outsd-icon pro-outsd-icon-right
                                fas
                                fa-arrow-right
                            "
                        onClick={() => controlSlide(-1)}
                    ></i>
                </div>
                <div className="row pro-outsd flex-nowrap" ref={main}>
                    {proState.length !== 0 &&
                        proState.map((item) => (
                            <div className="col l-2-5">
                                <div className="list-produce-item">
                                    <div
                                        className="produce-item-img"
                                        style={{
                                            backgroundImage: `url('${item.proImage[0].main.url}')`,
                                        }}
                                    ></div>
                                    <p className="produce-item-name">
                                        {item.proName}
                                    </p>
                                    <div
                                        className="
                                        produce-item-rate produce-item-rate-half
                                    "
                                    >
                                        <i className="rate-icon fas fa-star"></i>
                                        <i className="rate-icon fas fa-star"></i>
                                        <i className="rate-icon fas fa-star"></i>
                                        <i className="rate-icon fas fa-star"></i>
                                        <i className="rate-icon fas fa-star-half-alt"></i>
                                        <i className="rate-icon far fa-star"></i>
                                    </div>
                                    <div className="produce-item-prize">
                                        <p className="prize-old">
                                            {handleNum(item.proPrize)}
                                            <span className="prize-underline">
                                                đ
                                            </span>
                                        </p>
                                        <p className="prize-new">
                                            {handleNum(
                                                Math.ceil(
                                                    parseInt(item.proPrize) -
                                                        (parseInt(
                                                            item.proPrize
                                                        ) *
                                                            parseInt(
                                                                item.proPromo
                                                            )) /
                                                            100
                                                )
                                            )}
                                            <span className="prize-underline">
                                                đ
                                            </span>
                                        </p>
                                    </div>
                                    <div className="product-mark d-flex justify-content-between mb-2">
                                        <span
                                            className="product-mark-like"
                                            onClick={onLikeProduct}
                                        >
                                            {/* product-mark-liked */}
                                            <i className="far fa-heart cursor-pointer"></i>
                                            <i className="fas fa-heart cursor-pointer"></i>
                                        </span>
                                        <span className="produce-item-saled">
                                            Đã bán 34k
                                        </span>
                                    </div>
                                    <span className="produce-item-sal">
                                        {item.proPromo}%
                                    </span>
                                    <p className="produce-item-like">
                                        <i className="fas fa-check"></i>
                                        <span className="produce-item-text">
                                            Yêu Thích
                                        </span>
                                    </p>
                                </div>
                            </div>
                        ))}
                </div>
            </div>
        </>
    );
};

export default Slide;
