import React, { useEffect, useRef } from "react";

const ErrorForm = ({ msg }) => {
    console.log(msg);
    const fade = useRef();
    const addClass = () => {
        console.log([fade.current.className]);
    };

    useEffect(() => {
        console.log("use");
        if (msg.length) {
            addClass();
        }
    });

    return (
        <>
            {msg.map((item, index) => (
                <div key={index} className="msg" ref={fade}>
                    <i className="fa fa-check-circle" />
                    <div className="wrap-ms">
                        <span className="wrap-ms-title">Sucess</span>
                        <span className="wrap-ms-content">{item}</span>
                    </div>
                    <i className="fa fa-times"></i>
                </div>
            ))}
        </>
    );
};

export default ErrorForm;

export const MemoErrorForm = React.memo(ErrorForm);
