import {
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,
    USER_AUTH_FAIL,
    USER_REGISTER_REQUEST,
    USER_REGISTER_SUCCESS,
    ACCESSTOKEN,
    USER_LOGOUT,
    USER_UPLOAD_PROFILE,
    USER_UPLOAD_REQUEST,
} from "./typeAuth";
import axios from "axios";
import { SERVER } from "../../component/MainPages/helpers/key";

export const userLogin = (LoginFormData) => async (dispatch) => {
    try {
        dispatch({
            type: USER_LOGIN_REQUEST,
        });
        const { data } = await axios.post(
            `${SERVER}/user/login`,
            LoginFormData
        );

        if (data.success) {
            localStorage.setItem(ACCESSTOKEN, data.accessToken);
        }

        // await loadUser();

        dispatch({
            type: USER_LOGIN_SUCCESS,
        });

        return data;
    } catch (err) {
        dispatch({
            type: USER_AUTH_FAIL,
            payload: {
                isLoad: false,
                error: err.response.data.message || "Login is fail",
            },
        });
    }
};

export const userRegister = (RegisterFormData) => async (dispatch) => {
    try {
        dispatch({ type: USER_REGISTER_REQUEST });
        const { data } = await axios.post(
            `${SERVER}/user/register`,
            RegisterFormData
        );
        dispatch({
            type: USER_REGISTER_SUCCESS,
        });

        return data;
    } catch (err) {
        dispatch({
            type: USER_AUTH_FAIL,
            payload: {
                error: err.response.data.message,
            },
        });
    }
};
export const userLogout = () => async (dispatch) => {
    dispatch({ type: USER_LOGIN_REQUEST });
    localStorage.removeItem(ACCESSTOKEN);
    dispatch({ type: USER_LOGOUT });
};
export const sendProfileForm = (userInfo) => async (dispatch) => {
    try {
        dispatch({
            type: USER_UPLOAD_REQUEST,
        });

        const { data } = await axios.post(
            `${SERVER}/user/save/profile`,
            userInfo
        );

        dispatch({
            type: USER_UPLOAD_PROFILE,
            payload: {
                user: data.newUser,
            },
        });
    } catch (err) {
        dispatch({
            type: USER_AUTH_FAIL,
            payload: {
                error: err.response.data.message,
            },
        });
    }
};
