import React, { useContext } from "react";
import Signup from "../Form/Signup.js";
import Login from "../Form/Login.js";
import { userContext } from "../../Context/userProvider";
import { Redirect } from "react-router-dom";
import Loading from "../utils/Loading/Loading.js";
import Logout from "../Form/Logout.js";

const Auth = ({ authRender }) => {
    const {
        userState: { isLoad, isAuth },
    } = useContext(userContext);

    let body;

    if (isLoad) body = <Loading />;
    else if (isAuth) return <Redirect to="/" />;
    else
        body = (
            <>
                {authRender === "login" && <Login />}
                {authRender === "signup" && <Signup />}
            </>
        );
    console.log(body);
    return <>{body}</>;
};

export default Auth;
