import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import UserProvider from "./component/Context/userProvider";
import Auth from "./component/MainPages/Auth/Auth";
import MainPage from "./component/MainPages/Page";
import PageNotFound from "./component/MainPages/utils/NotFound/NotFound";
import Payment from "./component/MainPages/PayMent/Payment";

function App() {
    return (
        <UserProvider>
            <Router>
                <Switch>
                    <Route
                        path="/login"
                        render={(props) => (
                            <Auth {...props} authRender="login" />
                        )}
                    />
                    <Route
                        path="/signup"
                        render={(props) => (
                            <Auth {...props} authRender="signup" />
                        )}
                    />

                    <Route
                        exact
                        path="/"
                        render={(props) => (
                            <MainPage {...props} component="home" />
                        )}
                    />
                    <Route
                        exact
                        path="/product/:id"
                        render={(props) => (
                            <MainPage {...props} component="detail" />
                        )}
                    />
                    <Route
                        path="/products/cart"
                        render={(props) => (
                            <MainPage {...props} component="cart" />
                        )}
                    />
                    <Route path="/payment" component={Payment} />

                    <Route path="*" component={PageNotFound} />

                    {/* <Route
                        path="/cart"
                        render={(props) => (
                            <AuthLogin {...props} componentView="cart" />
                        )}
                    />
                    <Route path="/product/:id" component={Detail} />
                    <Route path="/test" component={FormTest} /> */}
                </Switch>
            </Router>
        </UserProvider>
    );
}

export default App;
